CREATE PROCEDURE [dbo].[SQSP_DYNAMIC_PIVOT]
    (
        @STATIC_COLUMN NVARCHAR(max),
        @PIVOT_COLUMN NVARCHAR(max),
        @VALUE_COLUMN NVARCHAR(max),
        @TABLE NVARCHAR(max),
        @AGGREGATE NVARCHAR(max) = null,
		@PROJECT_ID int
--@Query        NVARCHAR(max) OUTPUT,
--@TempTableDef NVARCHAR(max) OUTPUT,
--@colNameToDisplay NVARCHAR(max) OUTPUT
    )

AS


BEGIN

SET NOCOUNT ON;
declare @AVAIABLE_TO_PIVOT NVARCHAR(MAX),
        @SQLSTRING NVARCHAR(MAX),
        @PIVOT_SQL_STRING NVARCHAR(MAX),
        @TEMPVARCOLUMNS NVARCHAR(MAX),
        @TABLESQL NVARCHAR(MAX)

if isnull(@AGGREGATE,'') = '' 
    begin
        SET @AGGREGATE = 'MAX'
    end

IF OBJECT_ID('tempdb..#ResultTable') IS NOT NULL
BEGIN
   DROP TABLE #ResultTable;
END;


select mtc.ID as MaterialConsumptionID, mtc.ItemOfWorkBriefDescription,mtc.Quantity,m.Material+' ('+isnull(m.Unit,'')+')' as Material,mcd.materialValue 
into #ResultTable 
from MaterialConsumptiondetails mcd 
join MaterialConsumption mtc on mtc.ID = mcd.MaterialConsumptionID
join M_Materials m on mcd.MaterialId = m.MaterialId
where mtc.ProjectId=@PROJECT_ID

set @TABLE = '#ResultTable'

 SET @PIVOT_SQL_STRING =    'SELECT top 1 STUFF((SELECT distinct '', '' + CAST(''[''+CONVERT(NVARCHAR(MAX),'+ @PIVOT_COLUMN+')+'']''  AS NVARCHAR(max)) [text()]
                            FROM '+@TABLE+'
                            WHERE ISNULL('+@PIVOT_COLUMN+','''') <> ''''
                            FOR XML PATH(''''), TYPE)
                            .value(''.'',''NVARCHAR(MAX)''),1,2,'' '') as PIVOT_VALUES
                            from '+@TABLE+' ma
                            ORDER BY ' + @PIVOT_COLUMN + ''

							PRINT @PIVOT_SQL_STRING

declare @TAB AS TABLE(COL NVARCHAR(MAX) )


INSERT INTO @TAB EXEC SP_EXECUTESQL  @PIVOT_SQL_STRING, @AVAIABLE_TO_PIVOT 



SET @AVAIABLE_TO_PIVOT = (SELECT * FROM @TAB)


SET @TEMPVARCOLUMNS = (SELECT replace(@AVAIABLE_TO_PIVOT,',',' nvarchar(max) null,') + ' nvarchar(max) null')




SET @SQLSTRING = 'DECLARE @RETURN_TABLE TABLE ('+@STATIC_COLUMN+' NVARCHAR(max) NULL,'+@TEMPVARCOLUMNS+')  
                    INSERT INTO @RETURN_TABLE('+replace(@STATIC_COLUMN,'NVARCHAR(max) NULL','')+','+@AVAIABLE_TO_PIVOT+')

                    select * from (
                    SELECT ' + replace(@STATIC_COLUMN,'NVARCHAR(max) NULL','') + ' , ' + @PIVOT_COLUMN + ', ' + @VALUE_COLUMN + ' FROM '+@TABLE+' ) a

                    PIVOT
                    (
                    '+@AGGREGATE+'('+@VALUE_COLUMN+')
                    FOR '+@PIVOT_COLUMN+' IN ('+@AVAIABLE_TO_PIVOT+')
                    ) piv

                    SELECT * FROM @RETURN_TABLE FOR JSON AUTO'

PRINT @SQLSTRING
EXEC SP_EXECUTESQL @SQLSTRING 
--set @Query = @SQLSTRING
--set @TempTableDef = @STATIC_COLUMN+' NVARCHAR(255) NULL,'+@TEMPVARCOLUMNS
--set @colNameToDisplay = @AVAIABLE_TO_PIVOT
END

