					,ds.DescriptionOfTheItem as [Description],ds.ID as DsrDetailId
					,(select ISNULL(sum(ISNULL(Quantity,0)),0) from MeasurementSheet ms where ms.ProjectID=ash.ProjectID and ms.DSRDetailId=ash.DSRDetailsId and ms.DSRId=ash.DSRId) as Quantity
					,ash.FinalCompletedRate as Rate
					,ds.Unit
			from	RateAnalysis ash
			inner join ProjectDetails pd on pd.ID=ash.ProjectID 
			inner join DSRDetails ds on ds.ID=ash.DSRDetailsId and ash.DSRId=ds.DSRId
			and ash.ProjectID=@ProjectId
			union all
			select 1 as id, pd.ID as ProjectId, pd.ProjectName, dsr.DescriptionOfTheItem as [Description],dsr.ID as DsrDetailId, 1 as Quantity, Sum(ds.Amount) as Rate, '' as Unit
			from	LabTestDetails ds 
