			inner join ProjectDetails pd on pd.ID=@ProjectId
			inner join DSRDetails dsr on  dsr.DSRId=l.DSRId and IsNonDSRItem=1 and dsr.DescriptionOfTheItem like 'lab test%'
			and l.ProjectID=@ProjectId
			group by pd.ID,pd.ProjectName,dsr.DescriptionOfTheItem,dsr.ID
			) t
