		--	VALUES(1,'''+cast(@Description as nvarchar)+''','+cast(@Quantity as nvarchar)+','+cast(@Rate as nvarchar)+','''+cast(@Unit as nvarchar)+''','+cast(@Amount as nvarchar)+', 2,'+cast(@DsrDetailId as nvarchar)+','''+cast(GETDATE()as nvarchar)+''',1,'''+cast(GETDATE()as nvarchar)+''',1)')
			if exists (select 1 from AbstractSheet where ProjectId=@ProjectId and DSRId=@DSRId and DSRDetailId=@DSRDetailId)
			begin
					SELECT '1'
					update AbstractSheet
					set 
					[Description] = cast(@Description as nvarchar),
					Quantity = cast(@Quantity as nvarchar),
					Rate= cast(@Rate as nvarchar),
					Unit= cast(@Unit as nvarchar),
					Amount= cast(@Amount as nvarchar),
					ModifiedOn= getdate(),
					ModifiedBy=cast(@ModifiedBy as nvarchar)
					where ProjectId=@ProjectId and DSRId=@DSRId and DSRDetailId=@DSRDetailId
			end
			else
			begin
					SELECT '2'
					INSERT INTO AbstractSheet(ProjectId,[Description],Quantity,Rate,Unit,Amount,DSRId,DSRDetailId,CreatedOn,CreatedBy,ModifiedOn,ModifiedBy) 
					VALUES(@ProjectId,cast(@Description as nvarchar),cast(@Quantity as nvarchar),cast(@Rate as nvarchar),cast(@Unit as nvarchar),cast(@Amount as nvarchar), cast(@DSRId as nvarchar),cast(@DsrDetailId as nvarchar),GETDATE(),cast(@CreatedBy as nvarchar),GETDATE(),cast(@ModifiedBy as nvarchar))
			end
        --PRINT @product_name + CAST(@list_price AS varchar);
        FETCH NEXT FROM cursor_product INTO 
				@Description 
				,@Quantity
