CREATE PROCEDURE [dbo].[SQSPGetAllAbstractSheetDetails]	
( @ID as int)
AS
BEGIN

select  ROW_NUMBER() OVER (ORDER BY id DESC) AS id,ProjectID,ProjectName,Description,Quantity,Rate,Unit,
 (t.Quantity * t.Rate) as Amount from (

select  ash.Id as id, pd.ID as ProjectId
		,pd.ProjectName
		,ds.DescriptionOfTheItem as [Description]
		,(select ISNULL(sum(ISNULL(Quantity,0)),0) from MeasurementSheet ms where ms.ProjectID=ash.ProjectID and ms.DSRDetailId=ash.DSRDetailsId and ms.DSRId=ash.DSRId) as Quantity
		,ash.FinalCompletedRate as Rate
		,ds.Unit
from	RateAnalysis ash
inner join ProjectDetails pd on pd.ID=ash.ProjectID 
inner join DSRDetails ds on ds.ID=ash.DSRDetailsId and ash.DSRId=ds.DSRId
and ash.ProjectID=@ID
union all
select 1 as id, pd.ID as ProjectId, pd.ProjectName, dsr.DescriptionOfTheItem as [Description], 1 as Quantity, Sum(ds.Amount) as Rate, '' as Unit
from	LabTestDetails ds 
inner join LabTest l on l.ID=ds.LabTestId
inner join ProjectDetails pd on pd.ID=@ID
inner join DSRDetails dsr on  dsr.DSRId=l.DSRId and IsNonDSRItem=1 and dsr.DescriptionOfTheItem like 'lab test%'
and l.ProjectID=@ID
group by pd.ID,pd.ProjectName,dsr.DescriptionOfTheItem

) t


SELECT 'success'
END

