--  SQSPGetAllRecaptional 2
CREATE PROCEDURE [dbo].[SQSPGetAllRecaptional]	
(
@ProjectID as bigint
)
AS
BEGIN

if not exists(select 1 from Recaptional where ProjectID=@ProjectID)
begin
		declare @Total decimal(18,2)
		set @Total=(select top 1 AbstractAmount from AbstractSheet where ProjectID=@ProjectID)

		declare @DeductForRoyalty decimal(18,2)
		set @DeductForRoyalty=(select top 1 Amount from AbstractSheet where ProjectID=@ProjectID and DSRDetailId=(select ID from DSRDetails where IsNonDSRItem=1 and DescriptionOfTheItem like 'royalty%'))

		declare @DeductForTestingCharges decimal(18,2)
		set @DeductForTestingCharges=(select top 1 Amount from AbstractSheet where ProjectID=@ProjectID and DSRDetailId=(select ID from DSRDetails where IsNonDSRItem=1 and DescriptionOfTheItem like 'lab test%'))

		declare @TotalCostofWorkPortion decimal(18,2)
		set @TotalCostofWorkPortion=@Total-ISNULL(@TotalCostofWorkPortion,0)-isnull(@DeductForRoyalty,0)

		select 
		 @ProjectID as ProjectId 
		,@Total as WorkPortion
		,@Total as Total
		,@DeductForRoyalty as DeductForRoyalty
		,@DeductForTestingCharges as DeductForTestingCharges
		,@DeductForTestingCharges as TotalCostofWorkPortion
		,cast(0 as decimal(18,2)) as GST
		,cast(0 as decimal(18,2)) as LabourInsurancePer
		,cast(0 as decimal(18,2)) as ContingencyChargesPer
		,cast(0 as decimal(18,2)) as AdministrativeChargesPer
		,cast(0 as decimal(18,2)) as TotalGrossAmount 
		,cast(0 as decimal(18,2)) as Say 
END
ELSE
BEGIN
		select 
		 ProjectID as ProjectId 
		,WorkPortion as WorkPortion
		,TotalWorkPortion as Total
		,DeductForRoyalty as DeductForRoyalty
		,DeductForTestingCharges as DeductForTestingCharges
		,TotalCostofWorkPortion as TotalCostofWorkPortion
		,GSTPer as GST
		,LabourInsurancePer as LabourInsurancePer
		,ContigencyPer as ContingencyChargesPer
		,AdministrativePer as AdministrativeChargesPer
