    END;
 
CLOSE cursor_LeadInKMCharges;
 
DEALLOCATE cursor_LeadInKMCharges;
print @FinalQuery
set @FinalQuery = LEFT(@FinalQuery,LEN(@FinalQuery)-CHARINDEX('U', REVERSE(@FinalQuery),0));
EXEC(@FinalQuery)


