CREATE PROCEDURE [dbo].[SQSPIsAbstractSheetPresent]	
( @ID as int)
AS
BEGIN

declare @status bit

if exists( select top 1 * from  (
select  ash.Id as id, pd.ID as ProjectId
		,pd.ProjectName
		,ds.DescriptionOfTheItem as [Description]
		,(select sum(Quantity) from MeasurementSheet ms where ms.ProjectID=ash.ProjectID and ms.DSRDetailId=ash.DSRDetailsId and ms.DSRId=ash.DSRId) as Quantity
		,ash.FinalCompletedRate as Rate
		,ds.Unit
from	RateAnalysis ash
